import React, { createContext, useContext, useReducer, useEffect } from 'react';
import { authService } from '../services/authService';
import { getToken } from '../services/api';

const AuthContext = createContext();

// Auth reducer
const authReducer = (state, action) => {
  switch (action.type) {
    case 'LOGIN_START':
      return { ...state, loading: true, error: null };
    case 'LOGIN_SUCCESS':
      return { 
        ...state, 
        loading: false, 
        isAuthenticated: true, 
        user: action.payload.user,
        error: null 
      };
    case 'LOGIN_FAILURE':
      return { 
        ...state, 
        loading: false, 
        isAuthenticated: false, 
        user: null, 
        error: action.payload 
      };
    case 'LOGOUT':
      return { 
        ...state, 
        isAuthenticated: false, 
        user: null, 
        loading: false,
        error: null 
      };
    case 'UPDATE_USER':
      return { 
        ...state, 
        user: { ...state.user, ...action.payload } 
      };
    case 'CLEAR_ERROR':
      return { ...state, error: null };
    default:
      return state;
  }
};

const initialState = {
  isAuthenticated: false,
  user: null,
  loading: true,
  error: null,
};

export const AuthProvider = ({ children }) => {
  const [state, dispatch] = useReducer(authReducer, initialState);

  // Check for existing token on mount
  useEffect(() => {
    const checkAuth = async () => {
      console.log('üîç Checking authentication...');
      const token = getToken();

      if (token) {
        console.log('‚úÖ Token found in localStorage');
        try {
          // Check if token is expired (basic JWT decode)
          const tokenParts = token.split('.');
          if (tokenParts.length === 3) {
            const payload = JSON.parse(atob(tokenParts[1]));
            const currentTime = Date.now() / 1000;

            console.log('‚è∞ Token expires at:', new Date(payload.exp * 1000));
            console.log('‚è∞ Current time:', new Date());

            // If token is expired, logout immediately
            if (payload.exp && payload.exp < currentTime) {
              console.log('‚ùå Token expired, logging out');
              authService.logout();
              dispatch({ type: 'LOGOUT' });
              return;
            }
            console.log('‚úÖ Token is valid, checking profile...');
          }

          // Token is valid, get user profile
          const response = await authService.getProfile();
          console.log('‚úÖ Profile fetched successfully:', response.user?.name);
          dispatch({ 
            type: 'LOGIN_SUCCESS', 
            payload: { user: response.user } 
          });
        } catch (error) {
          console.log('‚ùå Auth check failed:', error.message);
          // Only logout if it's an auth error, not a network error
          if (error.message.includes('401') || error.message.includes('Unauthorized') || error.message.includes('Invalid token')) {
            console.log('üö™ Logging out due to auth error');
            authService.logout();
            dispatch({ type: 'LOGOUT' });
          } else {
            // For network errors, keep the user logged in but show they might be offline
            console.log('üåê Network error during auth check, keeping user logged in');
            dispatch({ type: 'LOGOUT' }); // Set loading to false
          }
        }
      } else {
        console.log('‚ùå No token found');
        dispatch({ type: 'LOGOUT' });
      }
    };

    checkAuth();
  }, []);

  const login = async (email, password) => {
    dispatch({ type: 'LOGIN_START' });
    try {
      const response = await authService.login({ email, password });
      dispatch({ 
        type: 'LOGIN_SUCCESS', 
        payload: { user: response.user } 
      });
      return response;
    } catch (error) {
      dispatch({ 
        type: 'LOGIN_FAILURE', 
        payload: error.message 
      });
      throw error;
    }
  };

  const register = async (name, email, password) => {
    dispatch({ type: 'LOGIN_START' });
    try {
      const response = await authService.register({ name, email, password });
      dispatch({ 
        type: 'LOGIN_SUCCESS', 
        payload: { user: response.user } 
      });
      return response;
    } catch (error) {
      dispatch({ 
        type: 'LOGIN_FAILURE', 
        payload: error.message 
      });
      throw error;
    }
  };

  const logout = () => {
    authService.logout();
    dispatch({ type: 'LOGOUT' });
  };

  const updateUser = (userData) => {
    dispatch({ type: 'UPDATE_USER', payload: userData });
  };

  const clearError = () => {
    dispatch({ type: 'CLEAR_ERROR' });
  };

  const value = {
    ...state,
    login,
    register,
    logout,
    updateUser,
    clearError,
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export default AuthContext;
